import React, { useState, useEffect } from "react";
import axios from "axios";
import { useApi } from "../api";
import "./comment.css";

type CommentProps = {
  questionid?: string;
  username: string;
  picture: string;
};

function Comments({ questionid, username, picture }: CommentProps) {
  const [answers, setAnswers] = useState<any[]>([]);
  const [contents, setContents] = useState("");
  const api = useApi();

  useEffect(() => {
    fetchAnswer();
  }, [api, questionid]);

  async function fetchAnswer() {
    const response = await axios.get(
      `https://taz.harding.edu/api/questions/${questionid}/answers?start=1`,
      {
        headers: {
          Authorization: `Bearer ${api.token}`,
        },
      }
    );

    const { answers } = response.data;
    setAnswers(answers);
    console.log(answers);
  }

  function createAns() {
    axios
      .post(
        `https://taz.harding.edu/api/questions/${questionid}/answers`,
        {
          author: username,
          contents: contents,
        },
        {
          headers: {
            Authorization: `Bearer ${api.token}`,
            "content-type": "application/json",
          },
        }
      )
      .then((response) => {
        console.log(response.data.json);
        fetchAnswer();
      })
      .catch((error) => {
        console.log(error);
      });
    setContents("");
  }

  async function likeAnswer(id: string, user: string) {
    try {
      const response = await axios.post(
        `https://taz.harding.edu/api/likes/${id}/${user}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${api.token}`,
            "content-type": "application/json",
          },
        }
      );
      console.log(response.data);
      fetchAnswer();
    } catch (e) {
      console.log(e);
    }
  }

  async function unlikeAnswer(id: string, user: string) {
    try {
      const response = await axios.delete(
        `https://taz.harding.edu/api/likes/${id}/${user}`,
        {
          headers: {
            Authorization: `Bearer ${api.token}`,
            "content-type": "application/json",
          },
        }
      );

      console.log(response.data);
      fetchAnswer();
    } catch (e) {
      console.log(e);
    }
  }

  return (
    <>
      {answers.map((answer) => {
        return (
          <div className="card2">
            <div className="comments">
              <div className="comment-react">
                {!answer.likedByUser ? (
                  <button
                    onClick={() => {
                      likeAnswer(answer.id, username);
                    }}
                  >
                    <img
                      className="heartImg"
                      alt="like icon"
                      src="data:image/png;base64,"
                    />
                  </button>
                ) : (
                  <button
                    onClick={() => {
                      unlikeAnswer(answer.id, username);
                    }}
                  >
                    <img
                      className="heartImg"
                      alt="unlike icon"
                      src="data:image/png;base64,"
                    />
                  </button>
                )}
                <hr />
                <span>{answer.likeCount}</span>
              </div>
              <div className="comment-container">
                <div className="user">
                  <div className="user-pic">
                    <a href={picture}>
                      <svg
                        fill="none"
                        viewBox="0 0 24 24"
                        height="20"
                        width="20"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          strokeLinejoin="round"
                          fill="#707277"
                          strokeLinecap="round"
                          strokeWidth="2"
                          stroke="#707277"
                          d="M6.57757 15.4816C5.1628 16.324 1.45336 18.0441 3.71266 20.1966C4.81631 21.248 6.04549 22 7.59087 22H16.4091C17.9545 22 19.1837 21.248 20.2873 20.1966C22.5466 18.0441 18.8372 16.324 17.4224 15.4816C14.1048 13.5061 9.89519 13.5061 6.57757 15.4816Z"
                        ></path>
                        <path
                          strokeWidth="2"
                          fill="#707277"
                          stroke="#707277"
                          d="M16.5 6.5C16.5 8.98528 14.4853 11 12 11C9.51472 11 7.5 8.98528 7.5 6.5C7.5 4.01472 9.51472 2 12 2C14.4853 2 16.5 4.01472 16.5 6.5Z"
                        ></path>
                      </svg>
                    </a>
                  </div>
                  <div className="user-info">
                    <span>{answer.author}</span>
                  </div>
                </div>
                <p className="comment-content">{answer.contents}</p>
              </div>
            </div>
          </div>
        );
      })}
      {/* REPLY BOX */}
      <div className="text-box">
        <div className="box-container">
          <textarea
            required
            placeholder="Reply"
            value={contents}
            onChange={(e) => setContents(e.target.value)}
          ></textarea>
          <div>
            <div className="formatting">
              <button
                type="submit"
                className="send"
                title="Send"
                onClick={createAns}
              >
                <svg
                  fill="none"
                  viewBox="0 0 24 24"
                  height="18"
                  width="18"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinejoin="round"
                    strokeLinecap="round"
                    strokeWidth="2.5"
                    stroke="#ffffff"
                    d="M12 5L12 20"
                  ></path>
                  <path
                    strokeLinejoin="round"
                    strokeLinecap="round"
                    strokeWidth="2.5"
                    stroke="#ffffff"
                    d="M7 9L11.2929 4.70711C11.6262 4.37377 11.7929 4.20711 12 4.20711C12.2071 4.20711 12.3738 4.37377 12.7071 4.70711L17 9"
                  ></path>
                </svg>
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Comments;
